import { createSelector } from 'reselect';

const COLLECTION_ID_PARAM_MAP = {
    hats : 1,
    sneakers: 2,
    jackets: 3,
    womens: 4,
    mens: 5
};

const shopData = state => state.shop;

export const shopCollection = createSelector(
    [shopData],
    shop => shop.collections
);

export const selectCollection = urlParam => createSelector(
    [shopCollection],
    collections => (collections.find(collection => {
            return collection.id === COLLECTION_ID_PARAM_MAP[urlParam];
        })
    )
);
